import React, {useState} from 'react';
import "./ExpenseItem.css";
import Card from '../ui/Card';
import Grid from '@material-ui/core/Grid';
import DeleteOutlinedIcon from '@material-ui/icons/DeleteOutlined';
import EditIcon from '@material-ui/icons/Edit';

import ExpenseDate from "./ExpenseDate";


function ExpenseItem(props) {

  const [expenseTitle, setTitle] = useState(props.title);
  let expenseAmount = props.amount;

  const [editCheck, changeEditCheck] = useState(false);

  const clickHandler = ()=>{
    console.log("clicked");
    setTitle('updated');
   } 

   const editClickHandler = ()=>{
    changeEditCheck(true);
   }

  return (

      <Card className="expense-item">

        <ExpenseDate date={props.date} />
        <div className="expense-item__description">
          <h2>{expenseTitle}</h2>
          <div className="expense-item__icons">{'$ '+expenseAmount}</div>
        </div>

         <div className='expense-item__icons '>
           <Grid item xs={8} onClick = {clickHandler}>
           <DeleteOutlinedIcon />
           </Grid>
         </div>

         <div className='expense-item__icons'>
           <Grid item xs={8} onClick = {editClickHandler}>
           <EditIcon />
           </Grid>
         </div>

          {editCheck && <p>sucees</p>}

      </Card>

  );
}

export default ExpenseItem;
