import "./ExpensesList.css";
import ExpenseItem from "./ExpenseItem";
import React, {useState} from "react";

const ExpensesList = (props) => {

/*   const[ItemsId, updateItemsId] = useState('');
  const[ItemsTitle, updateItemsTitle] = useState('');
  const[ItemsAmount, updateItemsAmount] = useState('');
  const[ItemsDate, updateItemsDate] = useState(''); */



 // console.log('-->objectItems'+Items)

  if (props.items.length === 0) {
      return(
        <h2 className='expenses-list__fallback'>No expenses Found</h2>)
  }

  const updateDelList = (dataId)=>{
    props.delPar(dataId)
  }


 return(<ul className='expenses-list'>
      {
          props.items.map((expense) => 
            {

                <ExpenseItem
              key={expense.id}
              id={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
              editParOne={props.editPar}
              delParOne={updateDelList}
            />

            }
            )
      }
    </ul>
  )
};

export default ExpensesList;
