import React, { useState } from "react";
import "./Expenses.css";
/* import ExpenseItem from "./ExpenseItem"; */
import Card from "../ui/Card";
import ExpenseFilter from "./ExpensesFilter";
import ExpensesList from "./ExpensesList";
import ExpensesChart from './ExpensesChart'

const Expenses = (props) => {
  const [setYear, changeYear] = useState("");
  const getOnChangeYear = (year) => {
    changeYear(year);
  };
  // console.log(setYear);

  const filteredExpenses = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === setYear;
  });

  const updateDelData = (dataId)=>{
    console.log(dataId)
  }

  return (
    <li>
    <Card className="expenses">
      <div>
        <ExpenseFilter setOnChangeYear={getOnChangeYear}></ExpenseFilter>
      </div>
      <ExpensesChart expenses={filteredExpenses}></ExpensesChart>
      <ExpensesList items={filteredExpenses} editPar={props.editOrg} delPar={updateDelData}></ExpensesList>
    </Card>
    </li>
  );
};

export default Expenses;
